import { Component, OnInit } from '@angular/core';
import { Transaction } from '../../services/points.service';

interface TransactionWithDetails extends Transaction {
  formattedAmount: string;
  formattedDate: string;
  icon: string;
  color: string;
}

@Component({
  selector: 'app-transactions',
  templateUrl: './transactions.component.html',
  styleUrls: ['./transactions.component.css']
})
export class TransactionsComponent implements OnInit {
  transactions: TransactionWithDetails[] = [];
  isLoading = false;
  filter = 'all'; // all, earn, spend, transfer

  constructor() {}

  ngOnInit(): void {
    this.loadTransactions();
  }

  loadTransactions(): void {
    this.isLoading = true;

    // Mock data - thay bằng service call thực tế
    const mockTransactions: Transaction[] = [
      {
        id: '1',
        type: 'EARN',
        amount: 100,
        description: 'Tích điểm mua hàng',
        timestamp: new Date().toISOString(),
        status: 'success'
      },
      {
        id: '2',
        type: 'TRANSFER',
        amount: 50,
        description: 'Chuyển điểm cho user123',
        timestamp: new Date(Date.now() - 86400000).toISOString(),
        status: 'success'
      },
      {
        id: '3',
        type: 'REDEEM',
        amount: 200,
        description: 'Đổi voucher giảm giá',
        timestamp: new Date(Date.now() - 172800000).toISOString(),
        status: 'success'
      }
    ];

    this.transactions = mockTransactions.map(tx => ({
      ...tx,
      formattedAmount: this.formatAmount(tx.amount, tx.type),
      formattedDate: this.formatDate(tx.timestamp),
      icon: this.getTransactionIcon(tx.type),
      color: this.getTransactionColor(tx.type)
    }));

    this.isLoading = false;
  }

  setFilter(filter: string): void {
    this.filter = filter;
    this.loadTransactions();
  }

  private formatAmount(amount: number, type: string): string {
    const prefix = type === 'EARN' ? '+' : type === 'REDEEM' ? '-' : '';
    return `${prefix}${amount.toLocaleString('vi-VN')}`;
  }

  private formatDate(timestamp: string): string {
    return new Date(timestamp).toLocaleDateString('vi-VN', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  }

  private getTransactionIcon(type: string): string {
    switch (type) {
      case 'EARN': return 'plus-circle';
      case 'REDEEM': return 'minus-circle';
      case 'TRANSFER': return 'arrow-right-circle';
      default: return 'circle';
    }
  }

  private getTransactionColor(type: string): string {
    switch (type) {
      case 'EARN': return 'text-green-600';
      case 'REDEEM': return 'text-red-600';
      case 'TRANSFER': return 'text-blue-600';
      default: return 'text-gray-600';
    }
  }

  get filteredTransactions(): TransactionWithDetails[] {
    if (this.filter === 'all') return this.transactions;
    return this.transactions.filter(tx =>
      tx.type.toLowerCase() === this.filter.toLowerCase()
    );
  }
}
<div class="container max-w-4xl mx-auto space-y-6 p-4">
  <div class="space-y-2">
    <h1 class="text-2xl font-bold">Lịch sử giao dịch</h1>
    <p class="text-gray-500">Xem tất cả các giao dịch điểm của bạn</p>
  </div>

  <!-- Filters -->
  <div class="flex space-x-2">
    <app-button
      [variant]="filter === 'all' ? 'default' : 'outline'"
      size="sm"
      (click)="setFilter('all')"
    >
      Tất cả
    </app-button>
    <app-button
      [variant]="filter === 'earn' ? 'default' : 'outline'"
      size="sm"
      (click)="setFilter('earn')"
    >
      Tích điểm
    </app-button>
    <app-button
      [variant]="filter === 'spend' ? 'default' : 'outline'"
      size="sm"
      (click)="setFilter('spend')"
    >
      Tiêu điểm
    </app-button>
    <app-button
      [variant]="filter === 'transfer' ? 'default' : 'outline'"
      size="sm"
      (click)="setFilter('transfer')"
    >
      Chuyển điểm
    </app-button>
  </div>

  <!-- Transactions List -->
  <div class="space-y-4">
    <div *ngFor="let transaction of filteredTransactions" class="bg-white rounded-lg border border-gray-200 p-4">
      <div class="flex items-center justify-between">
        <div class="flex items-center space-x-3">
          <!-- Icon -->
          <div class="flex-shrink-0">
            <svg class="h-8 w-8 {{ transaction.color }}" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <circle *ngIf="transaction.icon === 'circle'" cx="12" cy="12" r="10" stroke-width="2"/>
              <path *ngIf="transaction.icon === 'plus-circle'" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 9v3m0 0v3m0-3h3m-3 0H9m12 0a9 9 0 11-18 0 9 9 0 0118 0z"/>
              <path *ngIf="transaction.icon === 'minus-circle'" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M15 12H9m12 0a9 9 0 11-18 0 9 9 0 0118 0z"/>
              <path *ngIf="transaction.icon === 'arrow-right-circle'" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M13 7l5 5m0 0l-5 5m5-5H6"/>
            </svg>
          </div>

          <!-- Transaction Details -->
          <div>
            <h3 class="text-sm font-medium text-gray-900">{{ transaction.description }}</h3>
            <p class="text-sm text-gray-500">{{ transaction.formattedDate }}</p>
          </div>
        </div>

        <!-- Amount and Status -->
        <div class="text-right">
          <p class="text-lg font-semibold {{ transaction.color }}">
            {{ transaction.formattedAmount }}
          </p>
          <app-badge [variant]="transaction.status === 'success' ? 'success' : 'destructive'">
            {{ transaction.status === 'success' ? 'Thành công' : 'Thất bại' }}
          </app-badge>
        </div>
      </div>
    </div>

    <!-- Empty State -->
    <div *ngIf="filteredTransactions.length === 0 && !isLoading" class="text-center py-12">
      <svg class="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"/>
      </svg>
      <h3 class="mt-2 text-sm font-medium text-gray-900">Không có giao dịch</h3>
      <p class="mt-1 text-sm text-gray-500">Bạn chưa có giao dịch nào trong khoảng thời gian này.</p>
    </div>
  </div>

  <!-- Loading State -->
  <div *ngIf="isLoading" class="flex justify-center py-8">
    <div class="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
  </div>
</div>
